#!/bin/bash
set -euo pipefail

if [[ ! $# -eq 0 ]]; then
    ARGUMENT_1="$1"
else
    ARGUMENT_1="all"
fi

# receives a gpg key and returns its fingerprint
getFingerprint() {
    gpg --with-colons --import-options show-only --import 2>/dev/null | sed -n 2p | cut -d ':' -f 10
}

getFingerprintFromFile() {
    file=$1
    cat "$file" | getFingerprint
}

